var myapp=angular.module("day05",["ngRoute"]);myapp.config(["$routeProvider",function(e){e.when("/homepage",{templateUrl:"view/homepageView.html",controller:"homepageController"}).when("/musiclist",{templateUrl:"view/musiclistView.html",controller:"musiclistController"}).when("/music/:musicname",{templateUrl:"view/musicView.html",controller:"musicController"}).when("/phonelist",{templateUrl:"view/phonelistView.html",controller:"phonelistController"}).when("/phone/:phoneid",{templateUrl:"view/phoneView.html",controller:"phoneController"}).otherwise({redirectTo:"/homepage"})}]);
myapp.controller("homepageController",["$scope","$rootScope",function(o,e){e.title="银谷教育首页",o.logo="img/21052045.png",o.info="银谷时空网络教育科技有限公司是一家从事IT教育培训的专业机构。公司以就业和企业解决方案为导向、以人才为根本、以技术为核心，着力培养深受软件企业欢迎的实用型人才，解决IT企业最热门技术的难点。公司和长三角地区软件园区以及全国各地一流软件企业建立了长期合作关系，汇聚了中国IT培训最专业、最权威的教学团队。公司立足于中国IT教育技术的最前沿，为在校大学生、应届毕业生、社会就业者提供最热门、高实战性、高性价比的IT培训服务。经过八年来的发展，已培养数万IT各领域高端人才，已成为目前国内IT在线培训第一品牌。"}]);
myapp.controller("musicController",["$scope","$rootScope",function(o,t){t.title="音乐详情页面"}]);
myapp.controller("musiclistController",["$scope","$rootScope",function(o,t){t.title="音乐列表页面"}]);
myapp.controller("phoneController",["$scope","$rootScope","$routeParams","$http",function(o,e,t,n){e.title="手机详情页面",o.id=t.phoneid,n({url:"phones/"+o.id+".json",type:"get"}).success(function(e){o.phone=e})}]);
myapp.controller("phonelistController",["$scope","$rootScope","$http",function(o,t,e){t.title="手机列表页面",e({url:"phones/phones.json",type:"get"}).success(function(t){o.phonelist=t})}]);